'use strict';
const Signal = require('./signal');
const TYPES = require('./signalTypes').TYPES;

module.exports = class Metric extends Signal {

    /**
     *
     * @param {string} name
     * @param {string} source
     * @param {number} period
     * @param {Date} startDate
     * @param {Date} endDate
     * @param {?} values // TODO: clarify format
     */
    constructor(name, source, period, startDate, endDate, values) {

        super(TYPES.METRIC, name, source, startDate);
        this.payload = {
            capture_interval_s: period,
            date_started: startDate,
            date_ended: endDate,
            values
        };
    }
};
